import templateSource from "./template.hbs";
import Handlebars from "handlebars";



document.addEventListener("DOMContentLoaded", function () {
    const input = document.getElementById("bookmarkInput");
    const addBtn = document.getElementById("addBookmarkBtn");
    const list = document.getElementById("bookmarkList");

    let bookmarks = JSON.parse(localStorage.getItem("bookmarks")) || [];
    render();

    addBtn.addEventListener("click", () => {
        const url = input.value.trim();
        if (!url) return;

        bookmarks.push(url);
        save();
        render();
        input.value = "";
    });

    function save() {
        localStorage.setItem("bookmarks", JSON.stringify(bookmarks));
    }
  });

  function render() {
        list.innerHTML = "";
        bookmarks.forEach((url, index) => {
            const li = document.createElement("li");

            const link = document.createElement("a");
            link.href = url;
            link.target = "_blank";
            link.textContent = url;

            // –ö–Ω–æ–ø–∫–∞ —Ä–µ–¥–∞–≥—É–≤–∞–Ω–Ω—è
            const editBtn = document.createElement("button");
            editBtn.textContent = "‚úèÔ∏è";
            editBtn.style.backgroundColor = "#ffc107";
            editBtn.style.marginRight = "10px";
            editBtn.onclick = () => editBookmark(index);

            // –ö–Ω–æ–ø–∫–∞ –≤–∏–¥–∞–ª–µ–Ω–Ω—è
            const deleteBtn = document.createElement("button");
            deleteBtn.textContent = "üóë";
            deleteBtn.classList.add("delete");
            deleteBtn.onclick = () => removeBookmark(index);

            const btnWrapper = document.createElement("div");
            btnWrapper.appendChild(editBtn);
            btnWrapper.appendChild(deleteBtn);

            li.appendChild(link);
            li.appendChild(btnWrapper);
            list.appendChild(li);
        });
    }


    function removeBookmark(index) {
        bookmarks.splice(index, 1);
        save();
        render();
    }

    function editBookmark(index) {
        const newUrl = prompt("–ó–º—ñ–Ω–∏—Ç–∏ URL:", bookmarks[index]);
        if (newUrl) {
            bookmarks[index] = newUrl.trim();
            save();
            render();
        }
    }



document.addEventListener("DOMContentLoaded", function () {

    const usernameField = document.getElementById("username");
    const passwordField = document.getElementById("password");
    const saveButton = document.getElementById("saveBtn");

    const savedUsername = localStorage.getItem("username");
    const savedPassword = localStorage.getItem("password");

    if (savedUsername) usernameField.value = savedUsername;
    if (savedPassword) passwordField.value = savedPassword;


    saveButton.addEventListener("click", () => {
        localStorage.setItem("username", usernameField.value);
        localStorage.setItem("password", passwordField.value);

        alert("–î–∞–Ω—ñ –∑–±–µ—Ä–µ–∂–µ–Ω–æ!");
    });
});

const products = [
  {
    name: '–ù–æ—É—Ç–±—É–∫ Lenovo',
    price: 25000,
    description: '–ü–æ—Ç—É–∂–Ω–∏–π –Ω–æ—É—Ç–±—É–∫ –¥–ª—è —Ä–æ–±–æ—Ç–∏ —Ç–∞ –Ω–∞–≤—á–∞–Ω–Ω—è'
  },
  {
    name: '–°–º–∞—Ä—Ç—Ñ–æ–Ω Samsung Galaxy',
    price: 15000,
    description: '–°—É—á–∞—Å–Ω–∏–π —Å–º–∞—Ä—Ç—Ñ–æ–Ω –∑ –≤—ñ–¥–º—ñ–Ω–Ω–æ—é –∫–∞–º–µ—Ä–æ—é'
  },
  {
    name: '–ù–∞–≤—É—à–Ω–∏–∫–∏ Sony',
    price: 3500,
    description: '–ë–µ–∑–¥—Ä–æ—Ç–æ–≤—ñ –Ω–∞–≤—É—à–Ω–∏–∫–∏ –∑ —à—É–º–æ–∑–∞–≥–ª—É—à–µ–Ω–Ω—è–º'
  },
  {
    name: '–ö–ª–∞–≤—ñ–∞—Ç—É—Ä–∞ Logitech',
    price: 1200,
    description: '–ú–µ—Ö–∞–Ω—ñ—á–Ω–∞ –∫–ª–∞–≤—ñ–∞—Ç—É—Ä–∞ –¥–ª—è –≥–µ–π–º–µ—Ä—ñ–≤'
  },
  {
    name: '–ú–∏—à–∞ Razer',
    price: 800,
    description: '–Ü–≥—Ä–æ–≤–∞ –º–∏—à–∞ –∑ RGB –ø—ñ–¥—Å–≤—ñ—Ç–∫–æ—é'
  },
  {
    name: '–ú–æ–Ω—ñ—Ç–æ—Ä Dell',
    price: 8000,
    description: '27" –º–æ–Ω—ñ—Ç–æ—Ä –∑ —Ä–æ–∑–¥—ñ–ª—å–Ω–æ—é –∑–¥–∞—Ç–Ω—ñ—Å—Ç—é 4K'
  }
];

const template = Handlebars.compile(templateSource);

// üîπ –§—É–Ω–∫—Ü—ñ—è —Ä–µ–Ω–¥–µ—Ä–∞
function renderProducts(list) {
  const html = template(list);
  document.getElementById("app").innerHTML = html;
}

// üîπ –ü–µ—Ä—à–∏–π —Ä–µ–Ω–¥–µ—Ä
renderProducts(products);

// üîπ –ü–æ—à—É–∫
const searchInput = document.getElementById("searchInput");

searchInput.addEventListener("input", () => {
  const query = searchInput.value.toLowerCase();

  const filtered = products.filter(product =>
    product.name.toLowerCase().includes(query) ||
    product.description.toLowerCase().includes(query)
  );

  renderProducts(filtered);
});
